<?php		function addLector(){//форма додавання країни		$faculties = '<option value=""></option>';		$query="SELECT * FROM faculties";		$res=mysql_query($query);		while($row=mysql_fetch_array($res)){			$faculties .= "<option value=".$row['id'].">".$row['title_faculty']."</option>";		}		$GLOBALS['h1'] = "Додавання нового лектора";		$GLOBALS['content'] = <<<HTML		<div class="row-fluid">			<div class="span6">				<form class="well form-inline" method="POST" action="/?action=addLectorDo">					<div class="row-fluid">						<label>Прізвище</label>						<input type="text" name="surname" class="span12" value=""/>					</div>					<div class="row-fluid">						<label>Ім'я</label>						<input type="text" name="firstname" class="span12" value=""/>					</div>					<div class="row-fluid">						<label>Дата народження</label>						<input type="text" name="date_of_birth" class="span12" value=""/>					</div>					<div class="row-fluid">						<label>Науковий ступінь</label>						<select name="degree" class="span12">							<option value=""></option>							<option value="Кандидат наук">Кандидат наук</option>							<option value="Доктор наук">Доктор наук</option>						</select>					</div>					<div class="row-fluid">						<label>Посада</label>						<select name="post" class="span12">							<option value=""></option>							<option value="Асистент">Асистент</option>							<option value="Викладач">Викладач</option>							<option value="Доцент">Доцент</option>							<option value="Професор">Професор</option>						</select>					</div>					<div class="row-fluid">						<label>Факультет на якому працює</label>						<select name="id_faculty" class="span12">							{$faculties}						</select>					</div>					<div class="row-fluid">						<button type="submit" class="btn">Додати</button>					</div>				</form>			</div>		</div>HTML;	}		function addLectorDo(){//додавання країни		$surname = $_POST['surname'];		$firstname = $_POST['firstname'];		$array_date = explode('.', $_POST['date_of_birth']);		$date_of_birth = unix_date($array_date['0'].".".$array_date['1'].".".$array_date['2']);		$degree = $_POST['degree'];		$post = $_POST['post'];		$id_faculty = $_POST['id_faculty'];		$query = 'INSERT INTO lectors (surname, firstname, date_of_birth, degree, post, id_faculty_work) VALUES ("'.$surname.'", "'.$firstname.'", "'.$date_of_birth.'", "'.$degree.'", "'.$post.'", "'.$id_faculty.'")';		$result = mysql_query($query) or die(mysql_error());		header('Location: /?action=listLector', true, 303);	}		function listLector(){			if(isset($_GET['filter'])){			switch ($_GET['filter']){				case 'search':					$filter = "AND surname LIKE '%{$_GET['query']}%'";					break;				case 'surname':					$filter = 'ORDER BY surname '.$_GET['order'].', firstname '.$_GET['order'];					break;				case 'date_of_birth':					$filter = 'ORDER BY date_of_birth '.$_GET['order'];					break;								default: $filter = '';					break;			}		}else{			$filter = "";		}				$GLOBALS['h1'] = "Лектори";		$GLOBALS['content'] .= <<<TPL		<div class="row-fluid"><a href="/?action=addLector" class="btn btn-info right">Додати лектора</a></div>		<div class="row-fluid">			<table class="table table-bordered" id="dt_gal">				<thead>					<tr style="text-align:center;">						<th>№</th>						<th>Прізвище Ім'я <a href="/?action=listLector&filter=surname&order=ASC"><i class='icon-arrow-up'></i></a><a href="/?action=listLector&filter=surname&order=DESC"><i class='icon-arrow-down'></i></a></th>						<th>Дата народження <a href="/?action=listLector&filter=date_of_birth&order=ASC"><i class='icon-arrow-up'></i></a><a href="/?action=listLector&filter=date_of_birth&order=DESC"><i class='icon-arrow-down'></i></a></th>						<th>Науковий ступінь</th>						<th>Посада</th>						<th>Факультет</th>						<th></th>						<th>Предмети лектора</th>					</tr>				</thead>			<tbody>TPL;		$query="SELECT 					lectors.id,					lectors.surname,					lectors.firstname,					lectors.date_of_birth,					lectors.degree,					lectors.post,					lectors.id_faculty_work,					faculties.title_faculty				FROM 					lectors,					faculties				WHERE 					lectors.id_faculty_work=faculties.id				{$filter}";		$res=mysql_query($query);		$i = 0;		while($row=mysql_fetch_array($res)){			$i = $i+1;			$GLOBALS['content'] .= "				<tr>					<td>".$i."</td>					<td>".$row['surname']." ".$row['firstname']."</td>					<td>".date('d.m.Y', $row['date_of_birth'])."</td>					<td>".$row['degree']."</td>					<td>".$row['post']."</td>					<td>".$row['title_faculty']."</td>					<td><a href=\"?action=editLector&id=".$row['id']."\"><i class='icon-pencil'></i></a><a class='confirm del' href=\"?action=deleteLectorDo&id=".$row['id']."\"><i class='icon-trash'></i></a></td>					<td><a href=\"?action=subjectOfLector&id=".$row['id']."\"><i class='icon-user'></i></a></td>				<tr/>";		}		$GLOBALS['content'] .= '</tbody></table></div>';	}		function deleteLectorDo(){//видалення сторінки		$strSQL = ("DELETE FROM lectors WHERE id='{$_GET['id']}' LIMIT 1");		mysql_query($strSQL);				$strSQL = 'UPDATE subjects SET id_lector = "0" WHERE id_lector = "'.$_GET['id'].'"';		mysql_query($strSQL);				$strSQL = 'UPDATE faculties SET id_decan = "0" WHERE id_decan = "'.$_GET['id'].'"';		mysql_query($strSQL);		header("Location: /?action=listLector", true, 303);	}			function editLector(){//форма редагування сторінки		$faculties = '<option value=""></option>';		$query="SELECT * FROM faculties";		$res=mysql_query($query);		while($row=mysql_fetch_array($res)){			$faculties .= "<option value=\"".$row['id']."\">".$row['title_faculty']."</option>";		}				$query="SELECT * FROM lectors WHERE id = '{$_GET['id']}';";		$res=mysql_query($query);		$row=mysql_fetch_array($res);				$text_degree = '<option value=""></option>						<option value="Кандидат наук">Кандидат наук</option>						<option value="Доктор наук">Доктор наук</option>';				$var_degree = 'value="'.$row['degree'].'"';		$chose_degree = select($text_degree,$var_degree);				$text_post = '<option value=""></option>						<option value="Асистент">Асистент</option>						<option value="Викладач">Викладач</option>						<option value="Доцент">Доцент</option>						<option value="Професор">Професор</option>';				$var_post = 'value="'.$row['post'].'"';		$chose_post = select($text_post,$var_post);				$var_faculty = 'value="'.$row['id_faculty_work'].'"';		$chose_faculty = select($faculties,$var_faculty);				$date_of_birth = date('d.m.Y', $row['date_of_birth']);				$GLOBALS['h1'] = "Редактирование страницы";		$GLOBALS['content'] = <<<HTML		<div class="row-fluid">			<div class="span6">				<form class="well form-inline" method="POST" action="/?action=editLectorDo">					<div class="row-fluid">						<label>Прізвище</label>						<input type="text" name="surname" class="span12" value="{$row['surname']}"/>					</div>					<div class="row-fluid">						<label>Ім'я</label>						<input type="text" name="firstname" class="span12" value="{$row['firstname']}"/>					</div>					<div class="row-fluid">						<label>Дата народження</label>						<input type="text" name="date_of_birth" class="span12" value="{$date_of_birth}"/>					</div>					<div class="row-fluid">						<label>Науковий ступінь</label>						<select name="degree" class="span12">							{$chose_degree}						</select>					</div>					<div class="row-fluid">						<label>Посада</label>						<select name="post" class="span12">							{$chose_post}						</select>					</div>					<div class="row-fluid">						<label>Факультет на якому працює</label>						<select name="id_faculty" class="span12">							{$chose_faculty}						</select>					</div>					<div class="row-fluid">						<button type="submit" class="btn">Змінити</button>						<input type="hidden" name="id" value="{$row['id']}"/>					</div>				</form>			</div>		</div>HTML;	}	function editLectorDo(){//редагування сторінки		$array_date = explode('.', $_POST['date_of_birth']);		$date_of_birth = unix_date($array_date['0'].".".$array_date['1'].".".$array_date['2']);		$strSQL = 'UPDATE lectors SET surname = "'.$_POST["surname"].'",  firstname = "'.$_POST["firstname"].'",  date_of_birth = "'.$date_of_birth.'",  degree = "'.$_POST["degree"].'",  post = "'.$_POST["post"].'",  id_faculty_work = "'.$_POST["id_faculty"].'" WHERE id = "'.$_POST['id'].'"';		mysql_query($strSQL);		header("Location: /?action=editor_page&id={$_POST['id']}", true, 303);	}		function subjectOfLector(){		$query="SELECT 					subjects.title_subject,					subjects.id_lector,					subjects.id_faculty,					faculties.title_faculty,					faculties.id,					lectors.surname,					lectors.firstname				FROM 					subjects,					faculties,					lectors				WHERE					subjects.id_lector={$_GET['id']} AND subjects.id_faculty=faculties.id AND lectors.id={$_GET['id']}";							$res=mysql_query($query);		$i = 0;				$GLOBALS['content'] = '';		$GLOBALS['content'] .= <<<TPL		<div class="row-fluid">			<table class="table table-bordered" id="dt_gal">				<thead>					<tr style="text-align:center;">						<th>№</th>						<th>Факультет</th>						<th>Назва предмету</th>					</tr>				</thead>			<tbody>TPL;		while($row=mysql_fetch_array($res)){			if($i==0){				$surname = $row['surname'];				$firstname = $row['firstname'];			}			$i = $i+1;						$GLOBALS['h1'] = "Лектор: ".$surname." ".$firstname;			$GLOBALS['content'] .= "				<tr>					<td>".$i."</td>					<td>".$row['title_faculty']."</td>					<td>".$row['title_subject']."</td>				<tr/>";		}		$GLOBALS['content'] .= '</tbody></table></div>';	}	?>